mapper.select:
select * from ( select/*+ index_desc (product product_pk )*/ rownum rn , num, userid, goods, content, price, regdate,startdate,lastdate, category, goodsphoto, status from product where rownum <= #{pageNum} * #{amount}
order by regdate desc
order by regdate asc
order by price desc
) where rn > (#{pageNum}-1)*#{amount}

mapper.update:

update course set title=#{title}, content=#{content},
	price=#{price}, days=#{days}, regdate=sysdate,
	photo=#{photo} where num=#{num}
